function show(){if("Todos"==document.getElementById("emergency_state").value)for(var cards=document.getElementsByClassName("col-lg-4"),i=0;i<cards.length;i++)cards[i].style.display="block";else if("Pendientes"==document.getElementById("emergency_state").value)for(var cards=document.getElementsByClassName("col-lg-4"),i=0;i<cards.length;i++)0!=cards[i].getElementsByClassName("pending").length?cards[i].style.display="block":cards[i].style.display="none";else for(var cards=document.getElementsByClassName("col-lg-4"),i=0;i<cards.length;i++)0!=cards[i].getElementsByClassName("pending").length?cards[i].style.display="none":cards[i].style.display="block"}